import React, { useState } from "react";
import "./LandlordDashboard.css";

const LandlordDashboard = () => {
  const [houseDetails, setHouseDetails] = useState({
    location: "",
    price: "",
    category: "",
    image: null,
  });

  const [paymentConfirmed, setPaymentConfirmed] = useState(false);

  const handleChange = (e) => {
    setHouseDetails({ ...houseDetails, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    setHouseDetails({ ...houseDetails, image: e.target.files[0] });
  };

  const handlePayment = () => {
    alert("Send M-Pesa payment to 0710464858. Enter the transaction code to proceed.");
    setPaymentConfirmed(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!paymentConfirmed) {
      alert("Please complete the M-Pesa payment before submitting.");
      return;
    }
    alert("House submitted successfully! Waiting for admin approval.");
  };

  return (
    <div className="landlord-dashboard">
      <h2>Landlord Dashboard</h2>
      <form onSubmit={handleSubmit} className="house-form">
        <label>Location:</label>
        <input type="text" name="location" onChange={handleChange} required />

        <label>Price (KSh):</label>
        <input type="number" name="price" onChange={handleChange} required />

        <label>Category:</label>
        <select name="category" onChange={handleChange} required>
          <option value="">Select Category</option>
          <option value="Single Room">Single Room</option>
          <option value="Bedsitter">Bedsitter</option>
          <option value="One-Bedroom">One-Bedroom</option>
        </select>

        <label>Upload Image:</label>
        <input type="file" accept="image/*" onChange={handleImageUpload} required />

        {!paymentConfirmed ? (
          <button type="button" className="payment-button" onClick={handlePayment}>
            Pay via M-Pesa
          </button>
        ) : (
          <button type="submit" className="submit-button">
            Submit House
          </button>
        )}
      </form>
    </div>
  );
};

export default LandlordDashboard;
