import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // âœ… To navigate after login
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState(""); // Store email
  const [password, setPassword] = useState(""); // Store password
  const [error, setError] = useState(""); // Store login errors
  const navigate = useNavigate(); // âœ… Hook to navigate after login

  const handleLogin = (e) => {
    e.preventDefault(); // Prevent page refresh

    // ðŸ”¹ Example login logic (Replace this with Firebase/Django API)
    if (email === "admin@example.com" && password === "123456") {
      alert("Login successful! Redirecting..."); // Success message
      navigate("/"); // Redirect to homepage
    } else {
      setError("Invalid email or password"); // Show error message
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        {error && <p className="error-message">{error}</p>} {/* Show error */}
        <input
          type="email"
          placeholder="Enter Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
};

export default Login;
