import React, { useState, useEffect } from "react";
import { FaSearch, FaMapMarkerAlt } from "react-icons/fa";
import "./HeroSection.css";
import HouseListing from "./HouseListing";

const HeroSection = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [category, setCategory] = useState("");
  const [budget, setBudget] = useState("");
  const [filteredHouses, setFilteredHouses] = useState([]);
  const [userLocation, setUserLocation] = useState("");
  const [locationPermission, setLocationPermission] = useState(null);

  useEffect(() => {
    // ✅ Ask for location permission when the page loads
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          const locationName = await getLocationName(latitude, longitude);
          setUserLocation(locationName);
          setSearchTerm(locationName); // ✅ Automatically filter houses based on detected location
        },
        (error) => {
          console.error("Error getting location:", error);
          setLocationPermission("denied"); // ✅ Handle denied location permission
        }
      );
    } else {
      setLocationPermission("not supported");
    }
  }, []);

  // ✅ Convert latitude & longitude to a location name
  const getLocationName = async (lat, lon) => {
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`
      );
      const data = await response.json();
      return data.address?.city || data.address?.town || "Unknown Location";
    } catch (error) {
      console.error("Error fetching location name:", error);
      return "Unknown Location";
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    let filtered = allHouses.filter(
      (house) =>
        house.location.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (category === "" || house.category === category) &&
        (budget === "" || house.price <= parseInt(budget))
    );
    setFilteredHouses(filtered);
  };

  return (
    <div className="hero-container">
      <h1 className="hero-title">Find Your Dream Home</h1>
      <form className="hero-form" onSubmit={handleSearch}>
        <div className="location-wrapper">
          <FaMapMarkerAlt className="location-icon" />
          <input
            type="text"
            className="hero-input"
            placeholder="Enter location..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <select className="hero-select" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">All Categories</option>
          <option value="Single Room">Single Room</option>
          <option value="One-Bedroom">One-Bedroom</option>
          <option value="Bedsitter">Bedsitter</option>
        </select>
        <input
          type="number"
          className="hero-input budget-input"
          placeholder="Enter budget..."
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />
        <button type="submit" className="hero-button">
          <FaSearch /> Search
        </button>
      </form>

      {/* ✅ Show location permission message if denied */}
      {locationPermission === "denied" && (
        <p className="location-error">
          Location access denied. Please enter your location manually.
        </p>
      )}
      {locationPermission === "not supported" && (
        <p className="location-error">
          Your browser does not support location services. Please enter your location manually.
        </p>
      )}

      <HouseListing houses={filteredHouses.length > 0 ? filteredHouses : allHouses} />
    </div>
  );
};

export default HeroSection;
