import React, { useState } from "react";
import styles from "./LandlordDashboard.css";

const LandlordDashboard = () => {
  const [houses, setHouses] = useState([]);
  const [newHouse, setNewHouse] = useState({ location: "", price: "", category: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setNewHouse({ ...newHouse, [e.target.name]: e.target.value });
  };

  const handleAddHouse = () => {
    if (!newHouse.location || !newHouse.price || !newHouse.category) {
      setMessage("All fields are required!");
      return;
    }

    // Landlord must pay first before listing
    const confirmation = window.confirm("To list your house, please send KSh 500 to 0710464858 via M-Pesa and click OK after payment.");
    if (!confirmation) return;

    setHouses([...houses, { ...newHouse, id: houses.length + 1, status: "Pending Approval" }]);
    setMessage("House added successfully! Waiting for admin approval.");
    setNewHouse({ location: "", price: "", category: "" });
  };

  return (
    <div className={styles.dashboardContainer}>
      <h2>Landlord Dashboard</h2>
      
      <div className={styles.houseForm}>
        <h3>Add a House</h3>
        {message && <p className={styles.message}>{message}</p>}
        <input type="text" name="location" placeholder="Location" onChange={handleChange} value={newHouse.location} />
        <input type="number" name="price" placeholder="Price" onChange={handleChange} value={newHouse.price} />
        <select name="category" onChange={handleChange} value={newHouse.category}>
          <option value="">Select Category</option>
          <option value="Single Room">Single Room</option>
          <option value="One-Bedroom">One-Bedroom</option>
          <option value="Bedsitter">Bedsitter</option>
        </select>
        <button onClick={handleAddHouse} className={styles.addButton}>Submit House</button>
      </div>

      <div className={styles.houseList}>
        <h3>Your Houses</h3>
        {houses.length === 0 ? <p>No houses added yet.</p> : (
          houses.map((house) => (
            <div key={house.id} className={styles.houseItem}>
              <p><strong>Location:</strong> {house.location}</p>
              <p><strong>Price:</strong> KSh {house.price}</p>
              <p><strong>Category:</strong> {house.category}</p>
              <p><strong>Status:</strong> {house.status}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LandlordDashboard;
