import React from "react";
import { motion } from "framer-motion";
import { FaSearch } from "react-icons/fa";
import "./HeroSection.css";

const HeroSection = () => {
  return (
    <div className="hero-container">
      <motion.h1 className="hero-title">Find Your Dream Home</motion.h1>
      <motion.p className="hero-subtitle">
        Search for the best rental houses near you
      </motion.p>
      <motion.form className="hero-form">
        <input type="text" className="hero-input" placeholder="Enter location..." />
        <select className="hero-select">
          <option value="">All Categories</option>
          <option value="Single Room">Single Room</option>
          <option value="One-Bedroom">One-Bedroom</option>
          <option value="Bedsitter">Bedsitter</option>
        </select>
        <button type="submit" className="hero-button">
          <FaSearch />
        </button>
      </motion.form>
    </div>
  );
};

export default HeroSection;
