import React from "react";
import { motion } from "framer-motion";
import { FaSearch } from "react-icons/fa";

const HeroSection = () => {
  return (
    <div className="relative w-full h-screen flex flex-col items-center justify-center bg-gradient-to-r from-blue-600 to-indigo-700 text-white text-center p-6">
      <motion.h1 className="text-5xl font-extrabold mb-6 drop-shadow-lg">
        Pata Nyumba
      </motion.h1>
      <motion.form className="flex bg-white p-4 rounded-lg shadow-xl w-full max-w-md space-x-2">
        <input type="text" className="flex-1 p-3 rounded-lg border outline-none" placeholder="Enter location..." />
        <select className="p-3 rounded-lg border outline-none">
          <option value="">All Categories</option>
          <option value="Single Room">Single Room</option>
          <option value="One-Bedroom">One-Bedroom</option>
          <option value="Bedsitter">Bedsitter</option>
        </select>
        <button type="submit" className="bg-indigo-600 text-white p-3 rounded-lg flex items-center shadow-lg hover:bg-indigo-700">
          <FaSearch />
        </button>
      </motion.form>
    </div>
  );
};

export default HeroSection;
