import React from "react";
import { useNavigate } from "react-router-dom"; // ✅ Use navigate instead of another Router
import "./Login.css";

const Login = ({ onLogin }) => {
  const navigate = useNavigate(); // ✅ Allows redirection

  const handleLogin = () => {
    onLogin(); // ✅ Call the function to update authentication
    navigate("/"); // ✅ Redirect to main page after login
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
