import React, { useState } from "react";
import "./HouseListing.css";

const HouseListing = () => {
  const allHouses = [
    { id: 1, location: "Nairobi, Westlands", category: "Single Room", price: 20000, image: "house1.jpg" },
    { id: 2, location: "Nairobi, Kilimani", category: "One-Bedroom", price: 35000, image: "house2.jpg" },
    { id: 3, location: "Nairobi, Umoja", category: "Bedsitter", price: 12000, image: "house3.jpg" },
    { id: 4, location: "Nairobi, Karen", category: "Single Room", price: 25000, image: "house4.jpg" },
    { id: 5, location: "Nairobi, Runda", category: "One-Bedroom", price: 40000, image: "house5.jpg" },
    { id: 6, location: "Nairobi, Eastleigh", category: "Bedsitter", price: 15000, image: "house6.jpg" },
    { id: 7, location: "Nairobi, South B", category: "Single Room", price: 18000, image: "house7.jpg" },
    { id: 8, location: "Nairobi, Kileleshwa", category: "One-Bedroom", price: 45000, image: "house8.jpg" },
    { id: 9, location: "Nairobi, Kasarani", category: "Bedsitter", price: 13000, image: "house9.jpg" },
    { id: 10, location: "Nairobi, Ngong Road", category: "Single Room", price: 22000, image: "house10.jpg" },
    { id: 11, location: "Nairobi, Donholm", category: "One-Bedroom", price: 32000, image: "house11.jpg" },
    { id: 12, location: "Nairobi, Roysambu", category: "Bedsitter", price: 14000, image: "house12.jpg" },
    { id: 13, location: "Nairobi, Lavington", category: "Single Room", price: 27000, image: "house13.jpg" },
    { id: 14, location: "Nairobi, South C", category: "One-Bedroom", price: 38000, image: "house14.jpg" },
    { id: 15, location: "Nairobi, Kahawa", category: "Bedsitter", price: 16000, image: "house15.jpg" },
    { id: 16, location: "Nairobi, Ruaka", category: "Single Room", price: 24000, image: "house16.jpg" },
    { id: 17, location: "Nairobi, Thika Road", category: "One-Bedroom", price: 37000, image: "house17.jpg" },
    { id: 18, location: "Nairobi, Parklands", category: "Bedsitter", price: 15500, image: "house18.jpg" },
    { id: 19, location: "Nairobi, Upper Hill", category: "Single Room", price: 29000, image: "house19.jpg" },
    { id: 20, location: "Nairobi, CBD", category: "One-Bedroom", price: 40000, image: "house20.jpg" }
  ];

  const [visibleHouses, setVisibleHouses] = useState(allHouses);
  const [selectedHouse, setSelectedHouse] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingDetails, setBookingDetails] = useState({ name: "", phone: "", email: "" });

  const handleBookNow = (house) => {
    setSelectedHouse(house);
    setShowBookingForm(true);
  };

  const handleSubmitBooking = (e) => {
    e.preventDefault();
    alert(`Booking Confirmed for ${selectedHouse.category} at ${selectedHouse.location}`);
    setShowBookingForm(false);
    setBookingDetails({ name: "", phone: "", email: "" });
  };

  return (
    <div className="listing-container">
      <div className="background-image"></div> 
      {visibleHouses.length > 0 ? (
        visibleHouses.map((house) => (
          <div key={house.id} className="house-card">
            <img src={house.image} alt={house.category} className="house-image" />
            <div className="house-details">
              <h3 className="house-title">{house.category}</h3>
              <p className="house-text"><strong>Location:</strong> {house.location}</p>
              <p className="house-text"><strong>Price:</strong> KSh {house.price.toLocaleString()}</p>
              <button className="book-now-button" onClick={() => handleBookNow(house)}>Book Now</button>
            </div>
          </div>
        ))
      ) : (
        <p className="no-results">No houses available within this budget.</p>
      )}

      {showBookingForm && (
        <div className="booking-form-container">
          <div className="booking-form">
            <h2>Book {selectedHouse.category} at {selectedHouse.location}</h2>
            <form onSubmit={handleSubmitBooking}>
              <input
                type="text"
                placeholder="Your Name"
                value={bookingDetails.name}
                onChange={(e) => setBookingDetails({ ...bookingDetails, name: e.target.value })}
                required
              />
              <input
                type="tel"
                placeholder="Phone Number"
                value={bookingDetails.phone}
                onChange={(e) => setBookingDetails({ ...bookingDetails, phone: e.target.value })}
                required
              />
              <input
                type="email"
                placeholder="Email"
                value={bookingDetails.email}
                onChange={(e) => setBookingDetails({ ...bookingDetails, email: e.target.value })}
                required
              />
              <button type="submit">Confirm Booking</button>
              <button className="cancel-button" onClick={() => setShowBookingForm(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default HouseListing;
