import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { FaSearch, FaMapMarkerAlt } from "react-icons/fa";
import "./HeroSection.css";
import HouseListing from "./HouseListing";

const HeroSection = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [category, setCategory] = useState("");
  const [budget, setBudget] = useState("");
  const [userLocation, setUserLocation] = useState("");
  const [filteredHouses, setFilteredHouses] = useState([]);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          const locationName = await getLocationName(latitude, longitude);
          setUserLocation(locationName);
          setSearchTerm(locationName);
        },
        (error) => {
          console.error("Error getting location: ", error);
        }
      );
    }
  }, []);

  const getLocationName = async (lat, lon) => {
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`
      );
      const data = await response.json();
      return data.address?.city || data.address?.town || "Unknown Location";
    } catch (error) {
      console.error("Error fetching location name: ", error);
      return "Unknown Location";
    }
  };

  const handleSearch = () => {
    setFilteredHouses(
      allHouses.filter(
        (house) =>
          (category === "" || house.category === category) &&
          (budget === "" || house.price <= parseInt(budget))
      )
    );
  };

  return (
    <div className="hero-container page-container">
      <motion.h1 className="hero-title">PATA NYUMBA</motion.h1>
      <motion.p className="hero-subtitle">Find Your Dream Home</motion.p>
      <motion.form className="hero-form" onSubmit={(e) => e.preventDefault()}>
        <div className="location-wrapper">
          <FaMapMarkerAlt className="location-icon" />
          <input
            type="text"
            className="hero-input"
            placeholder="Enter location..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <select
          className="hero-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">All Categories</option>
          <option value="Single Room">Single Room</option>
          <option value="One-Bedroom">One-Bedroom</option>
          <option value="Bedsitter">Bedsitter</option>
        </select>
        <input
          type="number"
          className="hero-input budget-input"
          placeholder="Enter budget..."
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />
        <button type="button" className="hero-button" onClick={handleSearch}>
          <FaSearch />
        </button>
      </motion.form>
      <HouseListing houses={filteredHouses} />
    </div>
  );
};

export default HeroSection;
