import React, { useState } from "react";
import { FaMapMarkerAlt } from "react-icons/fa";
import { db } from "../firebaseConfig";
import { collection, addDoc } from "firebase/firestore";
import "./HouseListing.css";

const HouseListing = () => {
  const allHouses = [
    { id: 1, location: "Nairobi, Westlands", category: "Single Room", price: 20000, image: "house1.jpg", lat: -1.261, lng: 36.814 },
    { id: 2, location: "Nairobi, Kilimani", category: "One-Bedroom", price: 35000, image: "house2.jpg", lat: -1.286, lng: 36.817 }
  ];

  const [showForm, setShowForm] = useState(null);
  const [bookingData, setBookingData] = useState({ name: "", contact: "", date: "" });

  const handleBookNowClick = (id) => setShowForm(id);
  const handleCloseForm = () => setShowForm(null);

  const handleSubmitBooking = async (house) => {
    await addDoc(collection(db, "bookings"), {
      houseId: house.id, houseLocation: house.location, houseCategory: house.category,
      housePrice: house.price, tenantName: bookingData.name, tenantContact: bookingData.contact, bookingDate: bookingData.date, status: "Pending"
    });
    alert("Booking request submitted!");
    setShowForm(null);
  };

  return (
    <div className="listing-container">
      {allHouses.map((house) => (
        <div key={house.id} className="house-card">
          <img src={house.image} alt={house.category} className="house-image" />
          <div className="house-details">
            <h3>{house.category}</h3>
            <p><strong>Location:</strong> {house.location} 
              <FaMapMarkerAlt onClick={() => window.open(`https://www.google.com/maps?q=${house.lat},${house.lng}`, "_blank")} />
            </p>
            <p><strong>Price:</strong> KSh {house.price}</p>
            <button onClick={() => handleBookNowClick(house.id)}>Book Now</button>
            {showForm === house.id && (
              <div className="booking-form">
                <h3>Book {house.category}</h3>
                <form onSubmit={(e) => { e.preventDefault(); handleSubmitBooking(house); }}>
                  <input type="text" required placeholder="Name" value={bookingData.name} onChange={(e) => setBookingData({ ...bookingData, name: e.target.value })} />
                  <input type="text" required placeholder="Contact" value={bookingData.contact} onChange={(e) => setBookingData({ ...bookingData, contact: e.target.value })} />
                  <input type="date" required value={bookingData.date} onChange={(e) => setBookingData({ ...bookingData, date: e.target.value })} />
                  <button type="submit">Submit Booking</button>
                </form>
                <button onClick={handleCloseForm}>Close</button>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default HouseListing;
