import React, { useState } from "react";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [houses, setHouses] = useState([
    { id: 1, location: "Nairobi, Kilimani", category: "One-Bedroom", price: 35000, status: "Pending Approval" },
    { id: 2, location: "Nairobi, Westlands", category: "Single Room", price: 20000, status: "Approved" }
  ]);

  const handleApprove = (id) => {
    const updatedHouses = houses.map((house) =>
      house.id === id ? { ...house, status: "Approved" } : house
    );
    setHouses(updatedHouses);
  };

  const handleReject = (id) => {
    const updatedHouses = houses.filter((house) => house.id !== id);
    setHouses(updatedHouses);
  };

  return (
    <div className="dashboard-container">
      <h2>Admin Dashboard</h2>
      <h3>House Approvals</h3>
      <ul>
        {houses.map((house) => (
          <li key={house.id}>
            <strong>{house.category}</strong> - {house.location} - KSh {house.price} ({house.status})
            {house.status === "Pending Approval" && (
              <>
                <button className="approve-button" onClick={() => handleApprove(house.id)}>Approve</button>
                <button className="reject-button" onClick={() => handleReject(house.id)}>Reject</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
