import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("landlord"); // Default role is landlord
  const [error, setError] = useState("");
  const [resetEmail, setResetEmail] = useState(""); // State for forgot password
  const [showResetForm, setShowResetForm] = useState(false); // Toggle forgot password form

  const navigate = useNavigate();

  // ðŸ“Œ Handle Login
  const handleLogin = (e) => {
    e.preventDefault();

    // ðŸ“Œ Replace with your actual credentials
    const adminEmail = "youradminemail@example.com";
    const landlordEmail = "yourlandlordemail@example.com";

    if (email === adminEmail && password === "admin123" && role === "admin") {
      navigate("/admin-dashboard"); // Redirect to Admin Dashboard
    } else if (email === landlordEmail && password === "landlord123" && role === "landlord") {
      navigate("/landlord-dashboard"); // Redirect to Landlord Dashboard
    } else {
      setError("Invalid email, password, or role. Try again.");
    }
  };

  // ðŸ“Œ Handle Password Reset
  const handlePasswordReset = (e) => {
    e.preventDefault();
    if (!resetEmail) {
      setError("Please enter your email to reset your password.");
      return;
    }
    alert(`A password reset link has been sent to ${resetEmail}`);
    setShowResetForm(false); // Hide reset form after submitting
  };

  return (
    <div className="login-container">
      <h2>Login</h2>

      {/* ðŸ“Œ Show error message */}
      {error && <p className="error-message">{error}</p>}

      {/* ðŸ“Œ Login Form */}
      {!showResetForm ? (
        <form onSubmit={handleLogin}>
          <label>Email:</label>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label>Password:</label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <label>Role:</label>
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="landlord">Landlord</option>
            <option value="admin">Admin</option>
          </select>

          <button type="submit" className="login-button">Login</button>

          {/* ðŸ“Œ Forgot Password Link */}
          <p className="forgot-password" onClick={() => setShowResetForm(true)}>
            Forgot Password?
          </p>
        </form>
      ) : (
        // ðŸ“Œ Password Reset Form
        <form onSubmit={handlePasswordReset}>
          <h3>Reset Password</h3>
          <label>Email:</label>
          <input
            type="email"
            placeholder="Enter your email"
            value={resetEmail}
            onChange={(e) => setResetEmail(e.target.value)}
            required
          />
          <button type="submit" className="login-button">Reset Password</button>
          <p className="back-to-login" onClick={() => setShowResetForm(false)}>Back to Login</p>
        </form>
      )}
    </div>
  );
};

export default Login;
