import React, { useState, useEffect } from "react";
import { db } from "../firebaseConfig";
import { collection, getDocs, updateDoc, doc } from "firebase/firestore";
import "./AdminBookings.css";

const AdminBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      const querySnapshot = await getDocs(collection(db, "bookings"));
      setBookings(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchBookings();
  }, []);

  const updateBookingStatus = async (id, status) => {
    const bookingRef = doc(db, "bookings", id);
    await updateDoc(bookingRef, { status });
  };

  return (
    <div className="admin-bookings">
      <h2>Booking Requests</h2>
      {bookings.length === 0 ? <p>No bookings yet.</p> : (
        bookings.map((booking) => (
          <div key={booking.id} className="booking-card">
            <p><strong>Tenant:</strong> {booking.tenantName}</p>
            <p><strong>Contact:</strong> {booking.tenantContact}</p>
            <p><strong>House:</strong> {booking.houseCategory} in {booking.houseLocation}</p>
            <p><strong>Price:</strong> KSh {booking.housePrice.toLocaleString()}</p>
            <p><strong>Status:</strong> {booking.status}</p>
            <button onClick={() => updateBookingStatus(booking.id, "Approved")}>Approve</button>
            <button onClick={() => updateBookingStatus(booking.id, "Rejected")}>Reject</button>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminBookings;
