import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("landlord"); // Default role is landlord
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Dummy credentials for testing (replace with actual authentication logic)
    if (email === "ombongijared2.com" && password === "admin123" && role === "admin") {
      navigate("/admin-dashboard"); // Redirect to Admin Dashboard
    } else if (email === "landlord@example.com" && password === "landlord123" && role === "landlord") {
      navigate("/landlord-dashboard"); // Redirect to Landlord Dashboard
    } else {
      setError("Invalid email, password, or role. Try again.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleLogin}>
        <label>Email:</label>
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <label>Password:</label>
        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <label>Role:</label>
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="landlord">Landlord</option>
          <option value="admin">Admin</option>
        </select>

        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
};

export default Login;
