import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // ✅ Use navigate instead of another <Router>
import "./Login.css";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // ✅ Correct way to navigate

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // ✅ Simulating authentication (Replace with real logic later)
    if (email === "admin@pata-nyumba.com" && password === "admin123") {
      onLogin(); // ✅ Update auth state in `App.js`
      navigate("/"); // ✅ Redirect user after login
    } else {
      alert("Invalid email or password!"); // ❌ Replace this with a proper UI error message
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Enter Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
