import React, { useState } from "react";
import "./LandlordDashboard.css";

const LandlordDashboard = () => {
  const [houses, setHouses] = useState([
    { id: 1, location: "Nairobi, Kilimani", category: "One-Bedroom", price: 35000, status: "Pending Approval" },
    { id: 2, location: "Nairobi, Westlands", category: "Single Room", price: 20000, status: "Approved" }
  ]);
  const [newHouse, setNewHouse] = useState({ location: "", category: "", price: "" });
  const [paid, setPaid] = useState(false);

  const handlePayment = () => {
    // Simulate payment (Replace with M-Pesa API)
    setPaid(true);
    alert("Payment Successful! You can now list your house.");
  };

  const handlePostHouse = () => {
    if (!paid) {
      alert("You must pay a subscription before posting.");
      return;
    }

    const updatedHouses = [
      ...houses,
      { ...newHouse, id: houses.length + 1, status: "Pending Approval" }
    ];
    setHouses(updatedHouses);
    setNewHouse({ location: "", category: "", price: "" });
    alert("House submitted for admin approval.");
  };

  return (
    <div className="dashboard-container">
      <h2>Landlord Dashboard</h2>

      {/* Payment Section */}
      {!paid ? (
        <button className="pay-button" onClick={handlePayment}>Pay Subscription</button>
      ) : (
        <div className="post-house-form">
          <h3>Post New House</h3>
          <input type="text" placeholder="Location" value={newHouse.location} onChange={(e) => setNewHouse({ ...newHouse, location: e.target.value })} />
          <select value={newHouse.category} onChange={(e) => setNewHouse({ ...newHouse, category: e.target.value })}>
            <option value="">Select Category</option>
            <option value="Single Room">Single Room</option>
            <option value="One-Bedroom">One-Bedroom</option>
            <option value="Bedsitter">Bedsitter</option>
          </select>
          <input type="number" placeholder="Price" value={newHouse.price} onChange={(e) => setNewHouse({ ...newHouse, price: e.target.value })} />
          <button onClick={handlePostHouse}>Submit for Approval</button>
        </div>
      )}

      {/* Houses List */}
      <h3>My Listings</h3>
      <ul>
        {houses.map((house) => (
          <li key={house.id}>
            <strong>{house.category}</strong> - {house.location} - KSh {house.price} ({house.status})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LandlordDashboard;
