import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Login.module.css";

const Login = () => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between Login & Sign-Up
  const [formData, setFormData] = useState({ email: "", password: "", fullName: "", phone: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isLogin) {
      // **Login Logic**
      const storedLandlord = JSON.parse(localStorage.getItem("landlord"));
      if (storedLandlord && storedLandlord.email === formData.email && storedLandlord.password === formData.password) {
        navigate("/landlord-dashboard"); // Redirect to Landlord Dashboard
      } else if (formData.email === "admin@example.com" && formData.password === "admin123") {
        navigate("/admin-dashboard"); // Redirect to Admin Dashboard
      } else {
        setError("Invalid email or password. Try again.");
      }
    } else {
      // **Sign-Up Logic**
      if (!formData.fullName || !formData.email || !formData.password || !formData.phone) {
        setError("All fields are required!");
        return;
      }

      localStorage.setItem("landlord", JSON.stringify(formData));
      setSuccess("Account created successfully! You can now log in.");
      setTimeout(() => {
        setIsLogin(true);
      }, 2000);
    }
  };

  return (
    <div className={styles.authContainer}>
      <div className={styles.authCard}>
        <h2>{isLogin ? "Landlord Login" : "Landlord Sign Up"}</h2>
        {error && <p className={styles.errorMessage}>{error}</p>}
        {success && <p className={styles.successMessage}>{success}</p>}

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <>
              <label>Full Name:</label>
              <input type="text" name="fullName" placeholder="Enter your full name" onChange={handleChange} required />
              <label>Phone Number:</label>
              <input type="text" name="phone" placeholder="Enter your phone number" onChange={handleChange} required />
            </>
          )}
          
          <label>Email:</label>
          <input type="email" name="email" placeholder="Enter your email" onChange={handleChange} required />
          
          <label>Password:</label>
          <input type="password" name="password" placeholder="Enter your password" onChange={handleChange} required />
          
          <button type="submit" className={styles.authButton}>{isLogin ? "Login" : "Sign Up"}</button>
        </form>

        <p className={styles.togglePrompt}>
          {isLogin ? "Don't have an account?" : "Already have an account?"}
          <span onClick={() => setIsLogin(!isLogin)}> {isLogin ? "Sign Up" : "Login"}</span>
        </p>
      </div>
    </div>
  );
};

export default Login;
