import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // ✅ Added React Router
import Login from "./components/Login";
import FullPageScroll from "./components/FullPageScroll";
import { FaMapMarkerAlt } from "react-icons/fa";

// ✅ Added state to track if the user is authenticated
const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); 

  return (
    <Router> {/* ✅ Wrap the entire app with Router */}
      <Routes>
        {/* ✅ Route for Login Page */}
        <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} /> 
        
        {/* ✅ Show FullPageScroll if authenticated, otherwise redirect to login */}
        <Route path="/" element={isAuthenticated ? <FullPageScroll /> : <Login onLogin={() => setIsAuthenticated(true)} />} />  
      </Routes>
    </Router>
  );
};

// ✅ House Listing Component with Location Icon
export const HouseListing = ({ houses }) => {
  return (
    <div className="listing-container">
      {houses.map((house) => (
        <div key={house.id} className="house-card">
          <img src={house.image} alt={house.category} className="house-image" />
          <div className="house-details">
            <h3 className="house-title">{house.category}</h3>
            <p className="house-text">
              <strong>Location:</strong> {house.location}
              <a
                href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(house.location)}`}
                target="_blank"
                rel="noopener noreferrer"
                className="location-icon"
              >
                <FaMapMarkerAlt /> View on Map
              </a>
            </p>
            <p className="house-text"><strong>Price:</strong> KSh {house.price.toLocaleString()}</p>
            <button className="book-now-button">Book Now</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
