import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"; // ✅ Router imported only once
import Login from "./components/Login";
import FullPageScroll from "./components/FullPageScroll";
import HouseListing from "./components/HouseListing";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // ✅ Tracks login state

  return (
    <Router> {/* ✅ Router should only be defined ONCE in App.js */}
      <Routes>
        {/* ✅ Route for Login Page */}
        <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />

        {/* ✅ Show FullPageScroll if authenticated, otherwise show login */}
        <Route path="/" element={isAuthenticated ? <FullPageScroll /> : <Login onLogin={() => setIsAuthenticated(true)} />} />

        {/* ✅ Add other routes */}
        <Route path="/houses" element={<HouseListing />} />
      </Routes>
    </Router>
  );
};

export default App;
